program.vertex:
precision highp float;
attribute vec3 pos;
// varying float value;

void main(void) {
  gl_Position = vec4(pos, 1.0);
  gl_PointSize = 255.0;
  // value = 1.0;
}

program.fragment:
precision highp float;
uniform vec4 color;
uniform sampler2D font;

void main(void) {
  gl_FragColor = texture2D(font, gl_PointCoord);
}
